<?php
/**
 * Shortcodes
 *
 * @package luberon-sud
 */

defined( 'ABSPATH' ) || exit;


add_action( 'init', 'elabo_shortcodes' );

/**
 * Declare shortcodes
 */
function elabo_shortcodes() {
	add_shortcode( 'lst_random_villages_cards', 'elabo_random_villages_cards' );
	add_shortcode( 'lst_svg_map', 'elabo_map_svg' );
	add_shortcode( 'lst_ad_zone', 'elabo_ad_zone' );
	add_shortcode( 'lst_latest_articles', 'elabo_latest_articles' );
	add_shortcode( 'lst_posts_archive', 'elabo_posts_archive' );
}

/**
 * Archive of posts posts
 *
 * @return String Generated HTML
 */
function elabo_posts_archive() {
	$current_paged  = ( get_query_var( 'paged' ) ) ? absint( get_query_var( 'paged' ) ) : 1;
	$thumbnail_size = 'width-610';
	$args           = array(
		'posts_per_page' => 11,
		'post_type'      => 'post',
		'order'          => 'DESC',
		'paged'          => $current_paged,
	);
	$postslist      = new WP_Query( $args );

	if ( empty( $postslist->posts ) ) {
		return;
	}

	$html = '<div class="cards-listing-news">';
	while ( $postslist->have_posts() ) :
		$postslist->the_post();
		$class     = '';
		$thumbnail = get_the_post_thumbnail( null, $thumbnail_size );
		if ( empty( $thumbnail ) ) {
			$settings      = WPDAE()->plugin_options->get_settings();
			$attachment_id = (int) $settings['images']['placeholder_id'];
			$thumbnail     = wp_get_attachment_image( $attachment_id, 'apidae_archive_thumbnail' );
		}
		if ( $current_paged <= 1 && get_the_ID() === $postslist->posts[0]->ID ) {
			$class = ' format-landscape';
		}
		$permalink = get_the_permalink();
		$title     = get_the_title();
		$excerpt   = get_the_excerpt();
		$html     .= sprintf(
			'<article class="card-default%1$s">
				<figure>%2$s</figure>
				<div class="card-content">
						<div class="card-title">
								<h2><a href="%3$s" class="main-link">%4$s</a></h2>
						</div>
						%5$s
				</div>
			</article>',
			esc_attr( $class ),
			$thumbnail,
			esc_attr( $permalink ),
			esc_html( $title ),
			$excerpt
		);

	endwhile;
	wp_reset_postdata();
	$html .= '</div>';

	$nbr_pages = $postslist->max_num_pages;

	if ( $nbr_pages > 1 ) {
		$big      = 999999999;
		$paginate = paginate_links(
			array(
				'base'      => str_replace( $big, '%#%', esc_url( get_pagenum_link( $big ) ) ),
				'format'    => '?paged=%#%',
				'current'   => max( 1, get_query_var( 'paged' ) ),
				'total'     => $postslist->max_num_pages,
				'type'      => 'list',
				'next_text' => '>',
				'prev_text' => '<',
			)
		);
		$html    .= sprintf(
			'<nav class="pagination">%1$s</nav>',
			$paginate
		);
	}

	return $html;
}

/**
 * Show a list of villages cards
 *
 * @param Array $attr Shortcode attributes.
 * @return String
 */
function elabo_random_villages_cards( $attr ) {

	$limit = isset( $attr['limit'] ) ? intval( $attr['limit'] ) : -1;
	global $post;
	if ( ! isset( $post->post_parent ) ) {
		return;
	}
	$parent_page_id = isset( $post->post_parent ) ? $post->post_parent : $post->ID;

	$args = array(
		'post_type'      => 'page',
		'post_status'    => 'publish',
		'posts_per_page' => $limit,
		'post_parent'    => $parent_page_id,
		'post__not_in'   => array( $post->ID ),
		'order'          => 'ASC',
		'orderby'        => 'rand',
		'fields'         => 'ids',
		'no_found_row'   => true,
	);

	$results = new WP_Query( $args );

	if ( empty( $results->posts ) ) {
		return;
	}

	$cards = '<div class="list-villages-cards">';
	foreach ( $results->posts as $page_village_id ) {
		$permalink = get_the_permalink( $page_village_id );
		$excerpt   = get_the_excerpt( $page_village_id );
		$title     = get_the_title( $page_village_id );

		$thumbnail_id = get_post_thumbnail_id( $page_village_id );
		$size         = 'width-290';
		$img_src      = wp_get_attachment_image_url( $thumbnail_id, $size );
		$img_srcset   = wp_get_attachment_image_srcset( $thumbnail_id, $size );
		$thumbnail    = wp_get_attachment_image( $thumbnail_id, $size );
		$alt          = isset( get_post_meta( $page_village_id, '_wp_attachment_image_alt' )[0] ) ? get_post_meta( $page_village_id, '_wp_attachment_image_alt' )[0] : $title;
		$caption      = wp_get_attachment_caption( $thumbnail_id );

		$cards .= sprintf(
			'<article class="card-default">
				<figure>
					<img src="%1$s" srcset="%2$s" sizes="(max-width: 290px) 100vw, 290px" alt="%3$s" class="attachment-width-290 size-width-290 wp-post-image" loading="lazy">
				</figure>
				<div class="card-content">
					<div class="card-title">
						<span class="icon-location" aria-hidden="true"></span>
						<h2><a href="%6$s" class="main-link">%4$s</a></h2>
					</div>
					<p>%5$s</p>
				</div>
			</article>',
			esc_url( $img_src ),
			esc_attr( $img_srcset ),
			esc_html( $alt ),
			esc_html( $title ),
			wp_kses_post( $excerpt ),
			esc_url( $permalink )
		);
	}
	$cards .= '</div>';

	return $cards;
}

