/**
 * Ad Zone.
 *
 * @param Array $attr Shortcode attributes.
 * @return String Generated HTML
 */
function elabo_ad_zone( $attr ) {
	if ( ! isset( $attr['id'] ) ) {
		return;
	}

	$base_name = 'pub_type' . $attr['id'];

	$ad_zone_name = $base_name . '_campaign';

	$ad_zone = get_field( $ad_zone_name, 'option' );

	$random_key = array_rand( $ad_zone, 1 );

	$ad_name      = $ad_zone[ $random_key ][ $base_name . '_name' ];
	$ad_image_id  = $ad_zone[ $random_key ][ $base_name . '_image' ];
	$ad_image_src = wp_get_attachment_image_srcset( $ad_image_id, array( 400, 200 ) );
	$ad_image     = wp_get_attachment_image(
		$ad_image_id,
		array( 400, 200 ),
		false,
		array(
			'alt'    => $ad_name,
			'srcset' => $ad_image_src,
		)
	);
	$ad_link      = $ad_zone[ $random_key ][ $base_name . '_link' ];

	$ad = sprintf(
		'<a href="%1$s" data-adzone="%3$s" data-adname="%4$s">%2$s</a>',
		esc_url( $ad_link ),
		wp_kses_post( $ad_image ),
		esc_html( $ad_zone_name ),
		esc_html( $ad_name )
	);

	return $ad;
}

/**
 * Show a list the latests articles
 *
 * @param Array $attr Shortcode attributes.
 * @return String Generated HTML post list
 */
function elabo_latest_articles( $attr ) {

	$limit   = isset( $attr['limit'] ) ? intval( $attr['limit'] ) : -1;
	$display = isset( $attr['display'] ) ? $attr['display'] : 'cards';
	global $post;
	if ( ! isset( $post->post_parent ) ) {
		return;
	}

	$args = array(
		'post_type'      => 'post',
		'post_status'    => 'publish',
		'posts_per_page' => $limit,
		'post__not_in'   => array( $post->ID ),
		'order'          => 'DESC',
		'fields'         => 'ids',
		'no_found_row'   => true,
	);

	$results = new WP_Query( $args );

	if ( empty( $results->posts ) ) {
		return;
	}

	if ( 'block' === $display ) {
		return elabo_get_latest_articles_block( $results->posts );
	}

	if ( 'cards' === $display ) {
		return elabo_get_latest_articles_cards( $results->posts );
	}

	return;
}

/**
 * Returns formatted html for cards
 *
 * @param Array $posts Post array.
 * @return String Generated HTML
 */
function elabo_get_latest_articles_cards( $posts ) {

	$cards = '<div class="list-articles-cards">';
	foreach ( $posts as $article_id ) {
		$permalink     = get_the_permalink( $article_id );
		$excerpt       = get_the_excerpt( $article_id );
		$article_title = get_the_title( $article_id );

		$thumbnail_id = get_post_thumbnail_id( $article_id );
		$size         = $posts[0] == $article_id ? 'width-610' : 'width-290';

		if ( has_post_thumbnail( $article_id ) ) {
			$thumbnail = wp_get_attachment_image( $thumbnail_id, $size );
		} else {
			$settings      = WPDAE()->plugin_options->get_settings();
			$attachment_id = (int) $settings['images']['placeholder_id'];

			$thumbnail = wp_get_attachment_image( $attachment_id, 'apidae_archive_thumbnail' );
		}

		$alt     = isset( get_post_meta( $article_id, '_wp_attachment_image_alt' )[0] ) ? get_post_meta( $article_id, '_wp_attachment_image_alt' )[0] : $article_title;
		$caption = wp_get_attachment_caption( $thumbnail_id );

		$cards .= sprintf(
			'<article class="card-default">
				<figure>
					%1$s
				</figure>
				<div class="card-content">
					<div class="card-title">
						<h2><a href="%4$s" class="main-link">%2$s</a></h2>
					</div>
					<p>%3$s</p>
				</div>
			</article>',
			$thumbnail,
			esc_html( $article_title ),
			wp_kses_post( $excerpt ),
			esc_url( $permalink )
		);
	}
	$cards .= '</div>';

	return $cards;
}

/**
 * Returns formatted html for cards
 *
 * @param Array $posts Post array.
 * @return String Generated HTML
 */
function elabo_get_latest_articles_block( $posts ) {

	$block = '<div class="list-articles-block">';

	$thumbnail_id = get_post_thumbnail_id( $posts[0] );
	$img_src      = wp_get_attachment_image_url( $thumbnail_id, 'medium' );
	$img_srcset   = wp_get_attachment_image_srcset( $thumbnail_id, 'medium' );
	$alt          = isset( get_post_meta( $posts[0], '_wp_attachment_image_alt' )[0] ) ? get_post_meta( $posts[0], '_wp_attachment_image_alt' )[0] : '';
	$permalink    = get_the_permalink( $posts[0] );

	$block .= sprintf(
		'<figure>
			<a href="%4$s"><img src="%1$s" srcset="%2$s" sizes="(max-width: 290px) 100vw, 290px" alt="%3$s" class="attachment-width-290 size-width-290 wp-post-image" loading="lazy"></a>
		</figure>',
		esc_url( $img_src ),
		esc_attr( $img_srcset ),
		esc_html( $alt ),
		esc_url( $permalink ),
	);
	$block .= '<div class="article-list">';
	foreach ( $posts as $article_id ) {
		$permalink     = get_the_permalink( $article_id );
		$article_title = get_the_title( $article_id );

		$block .= sprintf(
			'<article class="article-title">
				<time>%1$s</time>
				<a href="%3$s" class="main-link">%2$s</a>
			</article>',
			get_the_date(),
			esc_html( $article_title ),
			esc_url( $permalink )
		);
	}
	$block .= '</div></div>';

	return $block;
}
